export default function BaseLayout() {
  // Default: dark mode enabled
  let [darkMode, setDarkMode] = useState(true);

  function handleToggleDarkMode() {
    const newValue = !darkMode;
    localStorage.setItem("darkMode", String(newValue));
    setDarkMode(newValue);
  }

  useEffect(() => {
    const saved = localStorage.getItem("darkMode");

    if (saved === "true") {
      setDarkMode(true);
    } else if (saved === "false") {
      setDarkMode(false);
    } else {
      // If nothing is saved yet, default to dark mode
      localStorage.setItem("darkMode", "true");
    }
  }, []);

  return (
    <Box className={darkMode ? Style.dark : Style.light}>
      <Grid container display="flex" flexDirection="column" minHeight="100vh" justifyContent="space_between">
        <Grid item>
          <Navbar darkMode={darkMode} handleClick={handleToggleDarkMode} />
        </Grid>

        <Grid item flexGrow={1}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/portfolio" element={<Portfolio />} />
          </Routes>
        </Grid>

        <Grid item>
          <Box component="footer" display="flex" flexDirection="column" alignItems="center" py="1.5rem" sx={{ opacity: 0.7 }} width="100%">
            Nimna Wijedasa
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}
